CREATE TABLE PATIENT (ID BIGINT NOT NULL, BIRTHDATE DATE, NAME VARCHAR(255), PATIENTID BIGINT, PRIMARY KEY (ID))
CREATE TABLE TREATMENT (ID BIGINT NOT NULL, TTYPE VARCHAR(31), DIAGNOSIS VARCHAR(255), patient_fk BIGINT, PRIMARY KEY (ID))
CREATE TABLE DRUGTREATMENT (ID BIGINT NOT NULL, DOSAGE FLOAT, DRUG VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE BILLINGRECORD (ID BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, AMOUNT FLOAT, DATE DATE, DESCRIPTION VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE DRUGTREATMENTRECORD (ID BIGINT NOT NULL, DATE DATE, DOSAGE FLOAT, DRUGNAME VARCHAR(255), SUBJECT_ID BIGINT, PRIMARY KEY (ID))
CREATE TABLE SUBJECT (ID BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, SUBJECTID BIGINT, PRIMARY KEY (ID))
ALTER TABLE TREATMENT ADD CONSTRAINT TREATMENTpatientfk FOREIGN KEY (patient_fk) REFERENCES PATIENT (ID)
ALTER TABLE DRUGTREATMENT ADD CONSTRAINT DRUGTREATMENT_ID FOREIGN KEY (ID) REFERENCES TREATMENT (ID)
ALTER TABLE DRUGTREATMENTRECORD ADD CONSTRAINT DRGTRTMNTRCRSBJCTD FOREIGN KEY (SUBJECT_ID) REFERENCES SUBJECT (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(15), PRIMARY KEY (SEQ_NAME))
